import React, { useState, useEffect } from 'react';
import "./Styling/Body.css";

function Body() {
  const [slideIndex, setSlideIndex] = useState(1);

  useEffect(() => {
    const interval = setInterval(() => {
      plusSlides(1);
    }, 10000); // Change slide every 10 seconds

    return () => clearInterval(interval);
  }, [slideIndex]);

  function plusSlides(n) {
    setSlideIndex((prevSlideIndex) => {
      let newIndex = prevSlideIndex + n;
      if (newIndex > 3) newIndex = 1;
      else if (newIndex < 1) newIndex = 3;
      return newIndex;
    });
  }

  function currentSlide(n) {
    setSlideIndex(n);
  }

  return (
    <div className="slideshow-container">
      <div className="slides" style={{ transform: `translateX(-${(slideIndex - 1) * 100}%)` }}>
        <div className="slidesFade">
          <img src="/produkter.png" style={{ width: '100%' }} alt="Slide 1" />
        </div>

        <div className="slidesFade">
          <img src="/guides.png" style={{ width: '100%' }} alt="Slide 2" />
        </div>

        <div className="slidesFade">
          <img src="/about.png" style={{ width: '100%' }} alt="Slide 3" />
        </div>
      </div>

      <a className="prev" onClick={() => plusSlides(-1)}>&#10094;</a>
      <a className="next" onClick={() => plusSlides(1)}>&#10095;</a>

      <div style={{ textAlign: "center", marginTop: "20px" }}>
        <span className={`dot ${slideIndex === 1 ? 'active' : ''}`} onClick={() => currentSlide(1)}></span>
        <span className={`dot ${slideIndex === 2 ? 'active' : ''}`} onClick={() => currentSlide(2)}></span>
        <span className={`dot ${slideIndex === 3 ? 'active' : ''}`} onClick={() => currentSlide(3)}></span>
      </div>
    </div>
  );
}

export default Body;
