function Box(props) {
  const { title, link, image } = props;
  return (
      <div className="Box">
          <a href={link}>
              <img src={image} alt="Placeholder" />
          </a>
          <div className="TextOverlay">
              <a href={link}><p>{title}</p></a>
          </div>
      </div>
  );
}



export default Body;
