import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Menu from "./Menu";
import Body from "./Body";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios"; // Import axios for making HTTP requests

export default function Router() {
  const [products, setProducts] = useState([]);
  const [mainCategories, setMainCategories] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state
  const [selectData, setSelectData] = useState([]);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/api/products"); // Fetching products data
        const data = response.data;
        setProducts(data);
        const categories = [...new Set(data.map((product) => product.main_category))];
        setMainCategories(categories);
        setLoading(false); // Set loading to false after data is fetched
      } catch (error) {
        console.error("Error fetching products data: ", error);
        setLoading(false); // Set loading to false in case of error
      }
    };
    fetchData();
  }, []);

  const axiosFetchData = async (processing) => {
    try {
      const response = await axios.get('http://localhost:4000/users');
      if (processing) {
        // Assuming you have a state variable named "setSelectData"
        setSelectData(response.data);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    axiosFetchData(true); // Call the function when component mounts
  }, []);

  const Layout = () => {
    if (loading) {
      return <div>Loading...</div>; // Render loading indicator
    }

    return (
      <>
        <Menu mainCategories={mainCategories} products={products} />
        <Header />
        <Body />
        <Footer />
      </>
    );
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />} />
      </Routes>
    </BrowserRouter>
  );
}
