import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Menu from "./Menu";
import Body from "./Body";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios"; // Import axios for making HTTP requests

export default function Router() {
  const [products, setProducts] = useState([]);
  const [mainCategories, setMainCategories] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/api/products"); // Fetching from the backend API
        const data = response.data;
        setProducts(data);

        // Extracting unique main categories from products
        const categories = [...new Set(data.map((product) => product.main_category))];
        setMainCategories(categories);
      } catch (error) {
        console.error("Error fetching products data: ", error);
      }
    };
    fetchData();
  }, []);

  const Layout = () => {
    return (
      <>
        <Menu mainCategories={mainCategories} products={products} />
        <Header />
        <Body />
        <Footer />
      </>
    );
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />} />
      </Routes>
    </BrowserRouter>
  );
}
