import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Menu from "./Menu";
import Body from "./Body";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";

export default function Router() {
  const [mainCategories, setMainCategories] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch main categories from the localhost/products endpoint
    const fetchMainCategories = async () => {
      try {
        const response = await axios.get('http://localhost:4000/products');
        // Assuming response.data contains an array of main categories
        setMainCategories(response.data);
      } catch (error) {
        console.log(error);
      }
    };

    // Fetch products data
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:4000/products');
        // Assuming response.data contains an array of products
        setProducts(response.data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchMainCategories();
    fetchProducts();
  }, []); // Empty dependency array ensures useEffect runs only once on mount

  const Layout = () => {
    return (
      <>
        <Header />
        <Menu mainCategories={mainCategories} products={products} /> {/* Pass products to Menu */}
        <Body />
        <Footer />
      </>
    );
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout />} />
      </Routes>
    </BrowserRouter>
  );
}
