import React, { useState, useEffect } from 'react';
import './Styling/Body.css';

function Body() {
  const [slideIndex, setSlideIndex] = useState(0); // Start index from 0
  const slides = ['/produkter.png', '/guides.png', '/about.png'];

  useEffect(() => {
    const interval = setInterval(() => {
      plusSlides(1);
    }, 10000); // Change slide every 10 seconds

    return () => clearInterval(interval);
  }, [slideIndex]);

  function plusSlides(n) {
    let newIndex = slideIndex + n;
    if (newIndex >= slides.length) newIndex = 0;
    else if (newIndex < 0) newIndex = slides.length - 1;
    setSlideIndex(newIndex);
  }

  function currentSlide(n) {
    setSlideIndex(n);
  }

  return (
    <div className="slideshow-container">
      {slides.map((slide, index) => (
        <div
          key={index}
          className={`slidesFade ${slideIndex === index ? 'active' : slideIndex < index ? 'enter' : 'exit'}`}
        >
          <img src={slide} style={{ width: '100%' }} />
        </div>
      ))}

      <a className="prev" onClick={() => plusSlides(-1)}>
        &#10094;
      </a>
      <a className="next" onClick={() => plusSlides(1)}>
        &#10095;
      </a>

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        {slides.map((_, index) => (
          <span
            key={index}
            className={`dot ${slideIndex === index ? 'active' : ''}`}
            onClick={() => currentSlide(index)}
          ></span>
        ))}
      </div>
    </div>
  );
}

export default Body;
